class HeaderController{constructor(){this.isNavOpen=!1,this.navToggle=document.getElementById("nav-toggle"),this.navClose=document.getElementById("nav-close"),this.navPanel=document.getElementById("nav-panel"),this.navToggleText=document.getElementById("nav-toggle-text"),this.overlay=document.getElementById("overlay"),this.init()}init(){this.navToggle&&this.navToggle.addEventListener("click",(()=>this.toggleNav())),this.navClose&&this.navClose.addEventListener("click",(()=>this.closeNav())),this.overlay&&this.overlay.addEventListener("click",(()=>this.closeNav())),document.addEventListener("keydown",(e=>{"Escape"===e.key&&this.closeNav()}))}toggleNav(){this.isNavOpen?this.closeNav():this.openNav()}openNav(){this.isNavOpen=!0,this.updateNavState()}closeNav(){this.isNavOpen=!1,this.updateNavState()}updateNavState(){this.navPanel.classList.toggle("open",this.isNavOpen),this.navPanel.setAttribute("aria-hidden",(!this.isNavOpen).toString()),this.navToggle.setAttribute("aria-expanded",this.isNavOpen.toString()),this.navToggleText.textContent=this.isNavOpen?"Close Navigation":"Open Navigation",this.navClose.setAttribute("aria-expanded",this.isNavOpen.toString()),this.overlay.classList.toggle("active",this.isNavOpen),this.overlay.setAttribute("aria-hidden",(!this.isNavOpen).toString()),this.manageFocus()}manageFocus(){if(this.isNavOpen){const e=this.navPanel.querySelector(".nav-link");e&&e.focus()}}}document.addEventListener("DOMContentLoaded",(()=>{new HeaderController}));