const THEME_STORAGE_KEY="theme",cachedTheme=localStorage.getItem("theme"),themePicker=document.getElementById("theme-picker");if(themePicker){const e=cachedTheme||"auto";themePicker.querySelector("input[checked]").removeAttribute("checked"),themePicker.querySelector(`input[value="${e}"]`).setAttribute("checked",""),themePicker.addEventListener("change",(e=>{const t=e.target.value;"auto"===t?localStorage.removeItem("theme"):localStorage.setItem("theme",t)}))}